import React, { Component } from 'react';
import NoteList from './components/NoteList/NoteList';
import RegistrationForm from './components/RegistrationForm/RegistrationForm';
import ListCategories from './components/ListCategories/ListCategories';

import './assets/App.css';
import './assets/index.css';

class App extends Component {
    constructor(){
        super();
        this.state = {
            notes: [],
            categories: [],
        };
    }

    createNote(title, text){
        const newNote = {title, text};
        const newArrayNotes = [...this.state.notes, newNote];
        const newState = {
            notes: newArrayNotes
        }

        this.setState(newState);
    }

    deleteNote(index){
        let arrayNotes = this.state.notes;
        arrayNotes.splice(index, 1);
        this.setState({notes: arrayNotes});
    }

    addCategorie(categorie_name){
        const newCategories = [...this.state.categories, categorie_name];
        const newState = {...this.state, categories: newCategories}
        this.setState(newState);
    }

    render(){
        return (
            <section className="content">
                <RegistrationForm 
                    createNote={this.createNote.bind(this)}
                    categories={this.state.categories}>
                </RegistrationForm>

                <main className="main-content">
                    <ListCategories 
                        categories={this.state.categories} 
                        addCategorie={this.addCategorie.bind(this)}>
                    </ListCategories>

                    <NoteList 
                        deleteNote={this.deleteNote.bind(this)}
                        notes={this.state.notes}>
                    </NoteList>
                </main>

            </section>
        );
    }
}

export default App;
