import React, { Component } from 'react';
import './style.css';

class RegistrationForm extends Component{
    constructor(props){
        super(props);
        this.title = '';
        this.text = '';
        this.category = 'Sem categoria';
    }

    _handleTitleChange(event){
        // stopPropagation - evitar a propagação do evento na arvore do html, no DOM.
        event.stopPropagation();
        this.title = event.target.value; 
    }

    _handleTextChange(event){
        event.stopPropagation();
        this.text = event.target.value;
    }

    _createNote(event){
        event.preventDefault();
        event.stopPropagation();
        this.props.createNote(this.title, this.text, this.category);
    }

    render(){
        return(
            <form className="registration-form" onSubmit={this._createNote.bind(this)}>
                <select className="registration-form_input">
                    <option>Sem Categoria</option>
                    {
                        this.props.categories.map((item, index) => {
                            return ( <option value="" key={index}>{item}</option> );     
                        })
                    }
                </select>

                <input 
                    type="text" 
                    placeholder="Título"
                    className="registration-form_input"
                    onChange={this._handleTitleChange.bind(this)}>
                </input>

                <textarea 
                    rows={15}
                    placeholder="Escreva sua nota.."
                    className="registration-form_input"
                    onChange={this._handleTextChange.bind(this)}>    
                </textarea>
                
                <button className="registration-form_input registration-form_submit">
                    Criar nota
                </button>
            </form>
        );
    }
}

export default RegistrationForm;
