export default class Categories{
    constructor(){
        this.categories = [];
        this._subscribers = [];        
    }

    register(func){
        // o inscrito será uma função: func
        this._subscribers.push(func);

    }

    createCategory(newCategory){
        this.categories.push(newCategory);
    }
}
